options.css:
* {
  font-family: Open Sans;
}

.box {
  border: solid #555 2px;
  border-radius: 10px;
  padding: 10px 20px;
}

options.onInit:
// Sets the text from customProperties
const htmlgraphicsText = htmlNode.getElementById('htmlgraphics-text');

if (htmlgraphicsText) {
  htmlgraphicsText.textContent = customProperties.text;

  // Change the text color based on the theme
  if (theme.isDark) {
    htmlgraphicsText.style.color = 'green';
  } else {
    htmlgraphicsText.style.color = 'red';
  }
}

loadurl = function(lcp) {
  switch (lcp) {
    case 'uk':
      window.location.href = 'd/4GmyvLInz/nap-processing-overview?orgId=1&refresh=5s&var-lcp=UK_PO1100000001&var-merchant=All';
      break;
    case 'bv':
      window.location.href = 'd/4GmyvLInz/nap-processing-overview?orgId=1&refresh=5s&var-lcp=BV_PO1100000002&var-merchant=All';
      break;
  }
}

options.onRender:
const getMetricByName = (metricName, series, noDataValue = 'No data') => {
    const filteredSeries = data.series.filter((series) => series.refId == metricName);
    if (filteredSeries.length > 0) {
        return filteredSeries[series].fields[1].values.buffer[1];
    }
    return noDataValue;
};

var payment_count_uk = getMetricByName('payment_cleared_uk',0);
var payment_value_uk = getMetricByName('payment_cleared_uk',1);

const wpuk_count = htmlNode.getElementById('wpuk_count');
const wpuk_value = htmlNode.getElementById('wpuk_value');
wpuk_count.textContent = payment_count_uk;
wpuk_value.textContent = payment_value_uk.toLocaleString();

var payment_count_bv = getMetricByName('payment_cleared_bv',0);
var payment_value_bv = getMetricByName('payment_cleared_bv',1);

const wpbv_count = htmlNode.getElementById('wpbv_count');
const wpbv_value = htmlNode.getElementById('wpbv_value');
wpbv_count.textContent = payment_count_bv;
wpbv_value.textContent = payment_value_bv.toLocaleString();
