options.css:
* {
  font-family: Open Sans;
}

.box {
  border: solid #555 2px;
  border-radius: 10px;
  padding: 10px 20px;
}

options.onInit:
// Sets the text from customProperties
const psc_output_path = htmlNode.getElementById('psc_output_path');
const psc_system = htmlNode.getElementById('psc_system');
const payment_cleared_topic = htmlNode.getElementById('payment_cleared_topic');
const payment_cleared_topic_text = htmlNode.getElementById('payment_cleared_topic_text');
const pricing_system = htmlNode.getElementById('pricing_system');
const payment_priced_topic = htmlNode.getElementById('payment_priced_topic');
const payment_priced_topic_text = htmlNode.getElementById('payment_priced_topic_text');
const pricing_failed_topic = htmlNode.getElementById('pricing_failed_topic');
const pricing_failed_topic_text = htmlNode.getElementById('pricing_failed_topic_text');
const pricing_input_path = htmlNode.getElementById('pricing_input_path');
const pricing_priced_output_path = htmlNode.getElementById('pricing_priced_output_path');
const pricing_failed_output_path = htmlNode.getElementById('pricing_failed_output_path');
const output_arrow = htmlNode.getElementById('Arrow2Lend');

if (psc_output_path) {
  // Change the text color based on the theme

  if (theme.isDark) {
    psc_system.style.stroke = 'white';
    psc_output_path.style.stroke = 'white';
    payment_cleared_topic_text.style.fill = 'white';
    payment_cleared_topic.style.stroke = 'white';
    pricing_system.style.stroke = 'white';
    payment_priced_topic.style.stroke = 'white';
    pricing_failed_topic.style.stroke = 'white';
    payment_priced_topic_text.style.fill = 'white';
    pricing_failed_topic_text.style.fill = 'white';
    pricing_input_path.style.stroke = 'white';
    pricing_priced_output_path.style.stroke = 'white';
    pricing_failed_output_path.style.stroke = 'white';
    output_arrow.style.stroke = 'white';
    output_arrow.style.fill = 'white';

  } 
}

options.onRender:
const psc_count = htmlNode.getElementById('psc_count');
const payment_cleared_count = htmlNode.getElementById('payment_cleared_count');
const payment_cleared_topic = htmlNode.getElementById('payment_cleared_topic');
const payment_priced_topic = htmlNode.getElementById('payment_priced_topic');
const payment_priced_count = htmlNode.getElementById('payment_priced_count');
const pricing_failed_count = htmlNode.getElementById('pricing_failed_count');
const pricing_failed_topic = htmlNode.getElementById('pricing_failed_topic');

const getMetricByName = (metricName, noDataValue = 'No data') => {
    const filteredSeries = data.series.filter((series) => series.refId == metricName);
    if (filteredSeries.length > 0) {
        return filteredSeries[0].fields[1].values.buffer[1];
    }
    return noDataValue;
};

var psc_total = getMetricByName('psc_count');
var payment_cleared_total  = getMetricByName('payment_cleared');
var priced_total  = getMetricByName('payment_priced');
var pricing_failed_total  = getMetricByName('payment_pricing_failed');
var pricing_total = priced_total + pricing_failed_total;

if (payment_cleared_total!=psc_total) {
    payment_cleared_topic.style.fill = "#f79740";
} else {
    payment_cleared_topic.style.fill = "#72d372";
}

if (pricing_total!=payment_cleared_total) {
    payment_priced_topic.style.fill = "#f79740";
    pricing_failed_topic.style.fill = "#f79740";

} else {
    payment_priced_topic.style.fill = "#72d372";
    pricing_failed_topic.style.fill = "#72d372";
}

psc_count.textContent = psc_total;
payment_cleared_count.textContent = payment_cleared_total;
payment_priced_count.textContent = priced_total;
pricing_failed_count.textContent = pricing_failed_total;
